public AASCalculatorContract add(int _valueA, int _valueB)
{
    AASCalculatorContract ret = new AASCalculatorContract();

    try
    {
        ret.parmStatus('OK');
        ret.parmResult(_valueA + _valueB);
        ret.parmMessage('');

    }
    catch (Exception::Error)
    {
        ret.parmStatus('KO');
        ret.parmResult(-1);
        ret.parmMessage("Something went wrong. It's a pity there are no logs!!");
    }

    return ret;
}

