public AASSmartCalculatorContract add(real _valueA, real _valueB)
{
    AASSmartCalculatorContract ret = new AASSmartCalculatorContract();

    try
    {
        ret.parmStatus('OK');
        ret.parmResult(_valueA + _valueB);
        ret.parmMessage('');

    }
    catch (Exception::Error)
    {
        ret.parmStatus('KO');
        ret.parmResult(-1);
        ret.parmMessage('Something went wrong');
    }

    return ret;
}

