public AASSmartCalculatorContract divide(real _valueA, real _valueB)
{
    AASSmartCalculatorContract ret = new AASSmartCalculatorContract();

    try
    {
        if (_valueB == 0)
        {
            throw Exception::Numeric;
        }

        ret.parmStatus('OK');
        ret.parmResult(_valueA / _valueB);
        ret.parmMessage('');
    }        
    catch (Exception::Numeric)
    {            
        ret.parmStatus('KO');
        ret.parmResult(-1);
        ret.parmMessage("You can't divide by zero...");         
    }
    catch
    {
        ret.parmStatus('KO');
        ret.parmResult(-1);
        ret.parmMessage('Something went wrong');
    }

    return ret;
}

